function sortByValue(obj){
    var sortedArray = [];
    for (const [key, value] of Object.entries(obj)) {
        sortedArray.push([key, value])
    }
    sortedArray.sort(function (a, b) {
        return b[1] - a[1];
    });

    const output = {}
    sortedArray.forEach(el => output[el[0]] = el[1]);
    return output;
}

